import { Grid, Typography, Box, TextField } from "@mui/material";
import { Fragment } from "react";


const InfoContent = (props) => {
  const data = props.data;
  const rows = [
    {
      name : "업체명",
      data : data.company_name,
    },
    {
      name : "대표자명",
      data : data.represent,
    },
    {
      name : "사업자 등록번호",
      data : data.corporate_number,
    },
    {
      name : "종목",
      data : data.description,
    },
    {
      name :  "담당자명",
      data : data.director,
    },
    {
      name :  "담당자 직함",
      data : data.position,
    },
    {
      name :  "연락처",
      data : data.phone_number,
    },
    {
      name :  "이메일",
      data : data.email,
    },
    {
      name :  "사업장 소재지",
      data : data.address,
    }
  ]

  const style = {fontWeight: 600,color: "#3855B3"}
  return (
    <Box
      sx={{
        color: "#919294",
        fontWeight: 400,
        fontSize: "12px",
        lineHeight: "140%",
        height: "210px"
      }}
      fullwidth
    >
      {/* <Box
        sx={{
          display: "flex",
          mb: "5px",
          ml: "20px",
        }}
      >
        <Box sx={{ width: "150px" }}>업체명</Box>
        <Box
          sx={{
            width: "100%",
            fontWeight: 600,color: "#3855B3",
          }}
        >
          주식회사 소더코드
        </Box>
      </Box> */}
      {
        rows.map((value, i) => (
          <Box sx={{ display: "flex", mb: "5px", ml: "20px", }}>
            <Box sx={{ width: "150px" (i===0 && style) }}>{value.name}</Box>
            <Box sx={{ width: "100%" }}>{value.data}</Box>
          </Box>
        ))
      }


    </Box>
  );
};

export default InfoContent;
