import { useRef, useState } from "react";
import {
    Box,
    Button,
    TextField,
    Typography,
    Snackbar,
    IconButton,
    Alert,
    Avatar
} from "@mui/material";
import Add from "@mui/icons-material/Add";
import CloseIcon from '@mui/icons-material/Close';

const CompanyText = {
    fontWeight: 600,
    fontSize: "13px",
    mb: "10px",
};

const TextFieldStyle = {
    padding: "11px",
    fontSize: "13px",
    fontWeight: 500,
    width: "238px",
    backgroundColor: '#fff'
};

const CompanyModal = (props) => {
    const data = props.data;

    const textRef = useRef([]);
    const [snackbarOpen, setSnackbarOpen] = useState(false);
    const [delectComplet, setDelectComplet] = useState(false);

    const defaultValues = [
        {
            name: "업체명",
            placeholder: "업체명을 입력해주세요",
            value: data && data.company_name,
        },
        {
            name: "대표자명",
            placeholder: "대표자명을 입력해주세요",
            value: data && data.represent,
        },
        {
            name: "사업자 등록번호",
            placeholder: "'-'없이 사업자 등록번호를 입력해주세요",
            value: data && data.corporate_number,
        },
        {
            name: "종목",
            placeholder: "사업장 종목을 입력해주세요",
            value: data && data.description,
        },
        {
            name: "담당자명",
            placeholder: "'-'없이 사업자 등록번호를 입력해주세요",
            value: data && data.director,
        },
        {
            name: "담당자 직함",
            placeholder: "사업장 종목을 입력해주세요",
            value: data && data.position,
        },
        {
            name: "연락처",
            placeholder: "'-'없이 연락처를 입력해주세요",
            value: data && data.phone_number,
        },
        {
            name: "이메일",
            placeholder: "이메일을 입력해주세요",
            value: data && data.email,
        },
        {
            name: "사업장 소재지",
            placeholder: "사업장 소재지를 입력해주세요",
            value: data && data.address,
        },
    ];

    const getContent = () => {
        const result = [];
        for (let i = 0; i < defaultValues.length - 1; i += 2) {
            result.push(
                <Box
                    key={i}
                    sx={{
                        display: "flex",
                        justifyContent: "space-evenly",
                        width: "100%",
                        mb: "16px",
                    }}
                >
                    <Box>
                        <Typography sx={CompanyText}>{defaultValues[i].name}</Typography>
                        <TextField
                            inputProps={{
                                style: TextFieldStyle,
                            }}
                            placeholder={defaultValues[i].placeholder}
                            value={defaultValues[i].value}
                            inputRef={(el) => (textRef.current[i] = el)}
                        />
                    </Box>

                    <Box>
                        <Typography sx={CompanyText}>{defaultValues[i + 1].name}</Typography>
                        <TextField
                            inputProps={{
                                style: TextFieldStyle,
                            }}
                            placeholder={defaultValues[i + 1].placeholder}
                            value={defaultValues[i + 1].value}
                            inputRef={(el) => (textRef.current[i + 1] = el)}
                        />
                    </Box>
                </Box>
            );
        }
        return result;
    };

    const showRefContent = () => {
        const rows = {
            company: textRef.current[0].value,
            representative: textRef.current[1].value,
            registerNum: textRef.current[2].value,
            BusinessType: textRef.current[3].value,
            DirectorPosition: textRef.current[4].value,
            DirectorName: textRef.current[5].value,
            phoneNum: textRef.current[6].value,
            email: textRef.current[7].value,
            address: textRef.current[8].value,
        };
    };


    const handleClose = (reason) => {
        if (reason === "clickaway") {
            return;
        }

        setSnackbarOpen(false);
    };


    return (
        <Box>
            <Snackbar
                anchorOrigin={{ vertical: "top", horizontal: "center" }}
                open={snackbarOpen}
                onClose={handleClose}
                autoHideDuration={60000}
                sx={{ width: "100%", height: 48, margin: "10px" }}
            >
                <Box>
                <Alert onClose={handleClose} severity="error" sx={{ width: "100%" }} variant="filled"
                    action={
                        <Box>
                            <Button color="inherit" size="small" onClick={()=>{ }}>
                                삭제
                            </Button>
                            <IconButton size="small" color="component" onClick={handleClose}>
                                <CloseIcon fontSize="inherit" color="ffffff" />
                            </IconButton>
                        </Box>
                    }>
                    업체는 삭제하면 복구가 불가능합니다. 삭제하시겠습니까?
                </Alert>
                <Alert
                severity="success"
                variant="filled"
                sx={{
                    width: "40%",
                    background: "#66BB6A",
                }}
            >
                삭제 완료!
            </Alert>

                </Box>





            </Snackbar>



            <Box
                sx={{
                    display: "flex",
                    alignItems: "center",
                    height: "71px",
                    ml: "20px",
                    fontWeight: 600,
                    fontSize: "18px",
                }}
            >
                {!data ? "업체등록" : "업체정보 수정"}
            </Box>
            <Box
                sx={{
                    flexDirection: "column",
                    justifyContent: "center",
                    height: "505px",
                    background: "#F8F8FA",
                    borderTop: "1px solid #E6E7E9",
                    borderBottom: "1px solid #E6E7E9",
                    padding: "10px",
                }}
            >
                <Box
                    sx={{
                        display: "flex",
                        alignItems: "center",
                        height: "72px",
                    }}
                >
                    <Box
                        sx={{
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            width: "48px",
                            height: "48px",
                            ml: "15px",
                        }}
                    >
                        <Avatar
                            sx={{ border: "1.5px dashed #E6E7E9", background: "#FFFFFF" }}
                        >
                            <Add sx={{ color: "#3855B3" }} />
                        </Avatar>
                    </Box>
                    <Button
                        variant="contained"
                        sx={{
                            borderRadius: "4px",
                            padding: "7px 12px",
                            fontWeight: 600,
                            fontSize: "13px",
                            width: "96px",
                            height: "32px",
                            mx: "16px",
                        }}
                    >
                        이미지 등록
                    </Button>
                    <Button
                        sx={{
                            width: "48px",
                            borderRadius: "4px",
                            padding: "0px 0px",
                            fontWeight: 600,
                            fontSize: "13px",
                            color: "#000000",
                            height: "32px",
                            border: "1px solid #E6E7E9",
                            "& .MuiBox-root": {
                                width: "48px",
                            },
                        }}
                    >
                        삭제
                    </Button>

                </Box>
                {getContent()}
                <Box sx={{ px: "13px" }}>
                    <Typography sx={CompanyText}>{defaultValues[8].name}</Typography>
                    <TextField
                        inputProps={{
                            style: {
                                padding: "11px",
                                fontSize: "13px",
                                fontWeight: 500,
                                width: "100%",
                                backgroundColor: '#fff'
                            },
                        }}
                        placeholder={defaultValues[8].placeholder}
                        inputRef={(el) => (textRef.current[8] = el)}
                        value={defaultValues[8].value}
                        sx={{ width: "100%" }}
                    ></TextField>
                </Box>
            </Box>
            <Box
                sx={{
                    display: "flex",
                    alignItems: "center",
                    height: "72px",
                }}
            >
                <Box>
                    {data && (
                        <Button
                            sx={{
                                borderRadius: "4px",
                                padding: "11px 12px",
                                fontWeight: 500,
                                fontSize: "14px",
                                color: "#DC2626",
                                width: "67px",
                                height: "40px",
                                border: "1px solid #DC2626",
                                ml: "16px",
                            }}
                            onClick={()=>{setSnackbarOpen(true)}}
                        >
                            삭제
                        </Button>
                    )}
                </Box>
                <Box
                    sx={{ width: "100%", display: "flex", justifyContent: "flex-end" }}
                >
                    <Button
                        sx={{
                            borderRadius: "4px",
                            padding: "11px 12px",
                            fontWeight: 500,
                            fontSize: "14px",
                            color: "#000000",
                            width: "67px",
                            height: "40px",
                            border: "1px solid #E6E7E9",
                        }}
                        onClick={() => { props.setModalOpen(false) }}
                    >
                        취소
                    </Button>
                    <Button
                        variant="contained"
                        sx={{
                            borderRadius: "4px",
                            padding: "11px 12px",
                            fontWeight: 500,
                            fontSize: "14px",
                            color: "#FFFFFF",
                            width: "67px",
                            height: "40px",
                            mx: "16px",
                        }}
                        onClick={showRefContent}
                    >
                        확인
                    </Button>


                </Box>
            </Box>
        </Box>
    );
};

export default CompanyModal;
