import React, {Fragment, lazy } from 'react';
import { Route, Routes, Navigate } from 'react-router-dom';
import Home from 'src/views/home/HomeView';


export const renderRoutes = (routes = []) => (
    <Routes>
  {
      routes.map((route, i) => {
        const RouteLayout = route.layout || Fragment;
        const RouteComponent = route.element;

        return (
          <Route key={i} path={route.path} render={(props) => (
                <RouteLayout>
                  {route.routes? renderRoutes(route.routes) : <RouteComponent {...props} />}
                </RouteLayout>
            )}
          />
        );
      })} 
    </Routes>
);

export const routes = [
  // 메인 페이지
  {
    path: '/home',
    element: lazy(() => import('src/views/home/HomeView'))
  },
  {
    path: '/404',
    element: lazy(() => import('src/views/errors/NotFoundView'))
  },

  // ------------------------------------------------------------------------------------------------------------------------------------------------ //
  //      Navigate
  // ------------------------------------------------------------------------------------------------------------------------------------------------ //
  {
    path: '*',
    routes: [
      {
        path: '/',
        element : <Navigate replace to="/home"/>
      },
      {
        element : <Navigate replace to="/404"/>
      }
    ]
  }
];
