import React, { Suspense, Fragment, lazy } from 'react';
import { Route, Routes } from 'react-router-dom';

export const renderRoutes = (routes = []) => (
    <Routes>
      {
      routes.map((route, i) => {
        const RouteLayout = route.layout || Fragment;
        const RouteComponent = route.component;

        return (
          <Route
            key={i}
            path={route.path}
            render={(props) => (
              // 라우터 자식으로 경로에 맞는 컴포넌트 렌더링
                <RouteLayout>
                  {/*  route가 Navigate 이라면 안에 routes 배열 순회 아니라면 element   */}
                  {route.routes? renderRoutes(route.routes) : <RouteComponent {...props} />}
                </RouteLayout>
            )}
          />
        );
      })} 

    </Routes>
);

export const routes = [
  // 메인 페이지
  {
    path: '/home',
    element: lazy(() => import('src/views/home/HomeView'))
  },
  {
    path: '/404',
    element: lazy(() => import('src/views/errors/NotFoundView'))
  },

  // ------------------------------------------------------------------------------------------------------------------------------------------------ //
  //      Navigate
  // ------------------------------------------------------------------------------------------------------------------------------------------------ //
  {
    path: '*',
    routes: [
      {
        path: '/',
        element : <Navigate replace to="/home"/>
      },
      {
        element : <Navigate replace to="/404"/>
      }
    ]
  }
];
