import React, { Suspense, Fragment, lazy } from 'react';
import { Route, Routes } from 'react-router-dom';

export const renderRoutes = (routes = []) => (
    <Routes>
      {
      routes.map((route, i) => {
        const Guard = route.guard || Fragment;
        const Layout = route.layout || Fragment;
        const Component = route.component;

        return (
          <Route
            key={i}
            path={route.path}
            exact={route.exact}
            render={(props) => (
                <Layout>
                  {/*  routes 배열 순차 순환 중 객체 안 routes가 있을 때 컴포넌트 랜더링*/}
                  {route.routes? renderRoutes(route.routes) : <Component {...props} />}
                </Layout>
            )}
          />
        );
      })} 

    </Routes>
);

export const routes = [
  // 메인 페이지
  {
    exact: true,
    layout: DashboardLayout,
    path: '/home',
    element: lazy(() => import('src/views/home/HomeView'))
  },
  {
    exact: true,
    path: '/404',
    element: lazy(() => import('src/views/errors/NotFoundView'))
  },
  {
    exact: true,
    guard: GuestGuard,
    path: '/login',
    element: lazy(() => import('src/views/auth/LoginView'))
  },
  {
    exact: true,
    path: '/login-unprotected',
    element: lazy(() => import('src/views/auth/LoginView'))
  },

  // ------------------------------------------------------------------------------------------------------------------------------------------------ //
  //      redirect
  // ------------------------------------------------------------------------------------------------------------------------------------------------ //
  {
    path: '*',
    routes: [
      {
        path: '/',
        element: () => <Redirect to="/home" />
      },
      {
        element: <Redirect to="/404" />
      }
    ]
  }
];
