import React from 'react';
import { useState } from "react";

import { Box, Typography, Button } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import Modal from '@mui/material/Modal';

import Header from 'src/components/Header';
import SearchField from 'src/components/SearchField'
import CompanyTable from 'src/components/CompanyInfo/CompanyTable';
import CompanyDetails from 'src/components/CompanyInfo/CompanyDetails';
import CompanyModal from 'src/components/CompanyInfo/CompanyModal';


import axios from "../../../utils/axios";
import { useQuery } from 'react-query';

const modalStyle = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  bgcolor: 'background.paper',
  width: "580px",
  height: "669px",
  borderRadius: "8px",
  backgroundColor: "#fff",
};

const CompanyInfoView = () => {
  const [companyIdx, setCompanyIdx] = useState(-1);
  const [viewData, setViewData] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);
  const [modalData, setModalData] = useState();

  const [searchValue, setSearchValue] = useState('');


  const query = useQuery(['companys'], async () => {
    const { data } = await axios.post('/companys/info', { company_type: '발주처' });
    return data.data
  }, {
    onSuccess: (data) => {
      // if(!init){
      setViewData(data);
      //   init = true
      // }
      
    }
  });

  if(query.isRefetching)
    console.log('asd')

  const showModal=(statue)=>{
    // statue = false : 업체 추가 버튼, true : 업체 수정 버튼 
    setModalData(statue === 'UPDATE' ? query.data.find(element => {return element.idx === statue}) : 'INSERT');
    setModalOpen(true);  
  }

  return (
    <>
      <Header>
        업체정보
      </Header>

      {
        query.isLoading ? (<div> Loading </div>) :
          (
            <Box sx={{ height: "100%" }}>
              <Box sx={{ display: "flex", alignItems: "center" }}>
                {/* <SearchField placeholder='업체명, 연락처 등 검색' companys={query.data} setViewData={setViewData} /> */}
                <Button variant="contained" sx={{fontWeight: 600, width: "94px",  height: "37px",  padding: "9.5px 0px", fontSize: "13px",
                  "& .MuiButton-startIcon": {
                    margin: "0px"
                  }
                }} startIcon={<AddIcon />} onClick={()=>{showModal(false)}}>
                  업체등록
                </Button>
              </Box>

              <Typography
                sx={{ fontWeight: 600, fontSize: "12px", mt: "19px", mb: "10px" }} >
                총 업체({viewData.length})
              </Typography>
              <Box sx={{ display: "flex", height: '100%' }} >
                <CompanyTable companys={viewData} setCompanyIdx={setCompanyIdx} showModal={showModal} />
                <CompanyDetails companyIdx={companyIdx} />
              </Box>

            {/* 모달 */}
              <Modal
                open={modalOpen}
                onClose={()=>{setModalOpen(false)}}>
                <Box sx={modalStyle}>
                  <CompanyModal data={modalData} setModalOpen={setModalOpen}/>
                </Box>
              </Modal>

            </Box>
          )
      }
    </>

  );
}

export default CompanyInfoView;
