  }
  const onChangeCreateAt = () =>{ 
    // 모달창 
  }

  useEffect(()=>{

  }, )

  return (
    <Box>
      <Header>
        업체정보
      </Header>

      {
        query.isLoading ? (<div> Loading </div>) : 
        (
          <Box>
            <Box sx={{ display: "flex", alignItems: "center" }}>
              <SearchField placeholder='업체명, 연락처 등 검색' companys={query.data} onSearchClick={(inputValue) => { alert(inputValue) }} />
            </Box>
            <Typography
              sx={{ fontWeight: 600, fontSize: "12px", mt: "19px", mb: "10px" }} >
              총 업체({query.data.length})
            </Typography>
            <Box sx={{ display: "flex" }} >
              <CompanyTable companys={query.data} onTableRowClick={(key)=>{setTableIndex(key)}}/>
              <CompanyDetails companyInfo={tableIndex !== -1 ?  query.data[tableIndex] : null}/>
            </Box>
          </Box>
        )
      }
    </Box>

  );
}

export default CompanyInfoView;
