import * as React from "react";
import { useState } from "react";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import {
  FormHelperText,
  Button,
  CssBaseline,
  TextField,
  Box,
  Container,
  Alert,
  Snackbar,
} from "@mui/material";
import axios from "../../utils/axios";

import { useNavigate } from "react-router-dom";

const theme = createTheme();

const SignIn = () => {
  const [emailError, setEmailError] = useState("");
  // const [passwordError, setPasswordState] = useState("");
  const [loginError, setloginError] = useState(false);
  const navigate = useNavigate();

  const onhandlePost = async (data) => {
    await axios
      .post("/account/signIn", {
        email: data,
      })
      .then((res) => {
        localStorage.setItem(
          "user",
          JSON.stringify(res.data.data, ["accessToken", "refreshToken"])
        );
        navigate(`/home`);
      })
      .catch(() => {
        setloginError(true);
      });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const email = new FormData(event.currentTarget);
    const emailRegex =
      /([\w-.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([\w-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)$/;
    if (!emailRegex.test(email))
      setEmailError("올바른 이메일 형식이 아닙니다.");
    else setEmailError("");

    // const passwordRegex =
    //   /^(?=.*[a-zA-Z])(?=.*[!@#$%^*+=-])(?=.*[0-9]).{8,25}$/;
    // if (!passwordRegex.test(userPassword))
    //   setPasswordState("숫자+영문자+특수문자 8자리");
    // else setPasswordState("");

    if (emailRegex.test(email)) {
      onhandlePost(email);
    }
  };

  const handleClose = ( reason ) => {
    if (reason === "clickaway") {
      return;
    }

    setloginError(false);
  };

  return (
    <ThemeProvider theme={theme}>
      <Snackbar
        anchorOrigin={{ vertical: "top", horizontal: "center" }}
        open={loginError}
        autoHideDuration={6000}
        onClose={handleClose}
      >
        <Alert onClose={handleClose} severity="error" sx={{ width: "100%" }}>
          사용자등록이 되어있지 않습니다. 관리자에게 문의해주세요.
        </Alert>
      </Snackbar>
      <Container component="main" maxWidth="xm">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 40,
            marginBottom: 50,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Box
            component="form"
            onSubmit={handleSubmit}
            noValidate
            sx={{ mt: 1, width: 500 }}
          >
            <h1>Login</h1>
            <TextField
              className="login_email"
              margin="normal"
              fullWidth
              required
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              autoFocus
              error={emailError !== "" || false}
            />
            <FormHelperText style={{color: "#D32F2F"}}>
              {emailError}
            </FormHelperText>

            <Button
              fullWidth
              type="submit"
              variant="contained"
              sx={{ mt: 3, mb: 2, height: 50, background: "#3855B3" }}
            >
              Login
            </Button>
          </Box>
        </Box>
      </Container>
    </ThemeProvider>
  );
};

export default SignIn;
