import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import Box from '@mui/material/Box'
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import Popover from '@mui/material/Popover';
import {useState} from 'react';

const UserInfoCard = () => {
    const [anchorEl, setAnchorEl] = useState(null);
    const [open, setOpen] = React.useState(false);

    const stringAvatar = (name) => {
        return {
            sx: {
                bgcolor: 'secondary.main',
                width: '32px',
                height: '32px',
                fontSize: '14px'
            },
            children: `${name.split('.')[0][0]}${name.split('.')[1][0]}`,
        };
    }

    const handleClick = (newPlacement) => (event) => {
        setAnchorEl(event.currentTarget);
      };

    return (

        <Box component='div' sx={{
            width: 210,
            height: 70,
            backgroundColor: 'primary.main',
            borderRadius: '8px',
        }}>

            <Box sx={{ px: 16, py: 19, display: 'flex' }} >
                <Avatar {...stringAvatar('Leo.moon')} />

                <Box sx={{ ml: 12, overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap', flexGrow: 1 }}>
                    <Box sx={{ fontWeight: 'bold', fontSize: '14px' }}> Leo.moon </Box>
                    <Box sx={{ fontSize: '12px' }}> 영업팀 </Box>
                </Box>

                <Box sx={{ borderRadius: '4px', backgroundColor: 'rgba(56, 85, 179, 0.04)', }}>
                    <IconButton onClick={handleClick()} aria-label="settings">
                        <KeyboardArrowDownIcon color="secondary" fontSize="small" />
                    </IconButton>
                </Box>
            </Box>

            <Popover
                open = {open}
                anchorEI={anchorEl}
                anchorOrigin={{
                    vertical: 'bottom',
                    horizontal: 'right',
                }}
                // transformOrigin={{
                //     vertical: 'top',
                //     horizontal: 'right',
                // }}
            >
                The content of the Popover.
            </Popover>

        </Box>
    );
}


export default UserInfoCard;
