import { useState } from "react";
import SearchIcon from "@mui/icons-material/Search";
import { Button, FormGroup, TextField, Box } from "@mui/material";
import CancelRoundedIcon from "@mui/icons-material/CancelRounded";
import { Autocomplete, createFilterOptions  } from "@mui/material";


const SearchField = (props) => {
  const [inputValue, setInputValue] = useState('');

  const filterOptions = createFilterOptions({
    limit: 2,
    matchFrom: 'start',
    stringify: option => option.label,
  });

  return (
    <Box sx={{ mr: "10px" }}>
      <FormGroup row>
        <Autocomplete
        size = {264}
          sx={{
            height: '37px',
            backgroundColor : '#fff',
            width: 264,
            "& .MuiInputBase-input": {
              fontSize: 13,
              fontWeight: 400,
            },
            "& .MuiOutlinedInput-root": {
              borderTopRightRadius: "0px",
              borderBottomRightRadius: "0px",
            },
          }}

          clearIcon={
            <CancelRoundedIcon
              sx={{
                width: "15px",
                height: "15px",
                color: "rgba(24, 24, 25, 0.42)",
                padding: "0px",
              }}
            />
          }
          autoHighlight
          filterSelectedOptions = {true}
          filterOptions={filterOptions}
          clearOnBlur
          freeSolo
          onInputChange={(event, newInputValue) => {
            setInputValue(newInputValue);
          }}

          options={inputValue !== ''  ? top100Films : [] }
          renderInput={(params) => 
          <TextField {...params} placeholder= {props.placeholder}
          sx={{height: 37, width: 264,}}
          InputProps={{
            ...params.InputProps,
            startAdornment: (
              <SearchIcon sx={{width: "13.5px", height: "13.5px", color: "rgba(24, 24, 25, 0.42)"}}  />
            ),
          }}
          />}
        />

        <Button
          sx={{
            borderTopLeftRadius: 0,
            borderBottomLeftRadius: 0,
            background: "#FAFAFA",
            border: "1px solid #C4C4C4",
            color: "#97979B",
            fontWeight: 600,
            fontSize: "13px",
            width: "72px",
            height: "37px",
            "&:hover": { backgroundColor: "rgba(56, 85, 179, 0.08)" },
          }}
          variant="contained"
          disableElevation
        >
          검색
        </Button>
      </FormGroup>
    </Box>
  );
};

const top100Films = [
  { label: "The Shawshank Redemption", year: 1994 },
  { label: "The Godfather", year: 1972 },
  { label: "The Godfather: Part II", year: 1974 },
  { label: "The Dark Knight", year: 2008 },
  { label: "12 Angry Men", year: 1957 },
  { label: "Schindler's List", year: 1993 },
];

export default SearchField;
