import React from 'react';
import { Box, Typography} from '@mui/material';
import { useState } from "react";

import Header from 'src/components/Header';
import SearchField from 'src/components/SearchField'
import CompanyTable from 'src/components/CompanyInfo/CompanyTable';
import CompanyDetails from 'src/components/CompanyInfo/CompanyDetails';

import axios from "../../../utils/axios";
import { useQuery } from 'react-query';

const CompanyInfoView = () => {
  const [companyNames, setCompanyNames] = useState('');
  const getCompanyAxios = async () => {
    const { data } = await axios.post('/companys/info', { company_type : '발주처'});
    return data;
  };

  const query = useQuery(['companys'], getCompanyAxios);

  const top100Films = [
    { title: "The Shawshank Redemption" },
    { title: "The Godfather"},
    { title: "The Godfather: Part II" },
    { title: "The Dark Knight" },
    { title: "12 Angry Men", year: 1957 },
    { title: "Schindler's List", year: 1993 },
  ];
  return (
    <Box>
      <Header>
        업체정보
      </Header>


        <Box sx={{ display: "flex", alignItems: "center" }}>
          <SearchField placeholder='업체명, 연락처 등 검색' companyNames={top100Films} onSearchClick={(inputValue)=>{alert(inputValue)}}/>
        </Box>
        <Typography
          sx={{ fontWeight: 600, fontSize: "12px", mt: "19px", mb: "10px" }} >
          총 업체(0)
        </Typography>
        <Box sx={{ display: "flex" }} >
        {
          query.isLoading 
        }
          <CompanyTable />
          <CompanyDetails />
        </Box>
    </Box>

  );
}

export default CompanyInfoView;
