import { createAction, handleActions } from 'redux-actions';
import produce from 'immer';


const initialState = {
    access_token : 'dasdasdasd',
};


const ACCESSTOKEN = 'token/ACCESSTOKEN';

export const setAccessToken = createAction(ACCESSTOKEN, access_token=> access_token);

////////////////////////////////////////
// Reducer
////////////////////////////////////////

const jwtToken = handleActions(
  {
    [ACCESSTOKEN]: (state, { payload: access_token }) =>
    produce(state, draft => {
      draft.access_token = access_token;
    }),
  },
  initialState
);

export default jwtToken;
