/* eslint-disable no-use-before-define */
import React from 'react';
import { Drawer, Box, List, ListItemIcon, ListItemButton, ListItemText } from '@mui/material'
import Divider from '@mui/material/Divider';
import ListItem from '@mui/material/ListItem';
import UserInfoCard from './UserInfoCard';

import CompanyInfoIcon from '@mui/icons-material/StoreOutlined';
import PurchaseMngIcon from '@mui/icons-material/LocalMallOutlined';
import InvntMngIcon from '@mui/icons-material/InboxOutlined';
import SalesMngIcon from '@mui/icons-material/ReceiptLongOutlined';

const items = [
  {
    title: '업체정보',
    icon : <CompanyInfoIcon/>
  },
  {
    title: '구매관리',
    icon : <PurchaseMngIcon/>
  },
  {
    title: '재고관리',
    icon : <InvntMngIcon/>
  },
  {
    title: '영업관리',
    icon : <SalesMngIcon/>
  },
]

const NavBar = () => {
  const [selectedIndex, setSelectedIndex] = React.useState(-1);
  const handleListItemClick = (event, index) => {
    setSelectedIndex(index);
  };
  return (
    <Box>
      <Drawer
        sx={{width: 250,flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: 250,
            boxSizing: 'border-box',
            border : 0,
          },
        }}
        variant="permanent"
        anchor="left">

        <List>
          <Box sx={{pl : 20, py : 22, pr : 88.88}}>
            <img src="nav_logo.png" style={{width: "100%"}}/>
          </Box>
        <Divider />
        <UserInfoCard/>
        {/* 아이템  */}
          {
            items.map((value, i) => (
              <ListItemButton key={i} sx={{px : 20}} 
              selected ={selectedIndex === i}
              onClick={(event) => handleListItemClick(event, i)}>
                <ListItemIcon>
                  {value.icon}
                </ListItemIcon>
                <ListItemText sx={{fontSize : '14px'}} primary={value.title} />
              </ListItemButton>
            ))
          }

        </List>

      </Drawer>
    </Box>

  );
};

export default NavBar;
