import React from 'react';
import { Box, Typography } from '@mui/material';
import { useEffect, useState } from "react";

import Header from 'src/components/Header';
import SearchField from 'src/components/SearchField'
import CompanyTable from 'src/components/CompanyInfo/CompanyTable';
import CompanyDetails from 'src/components/CompanyInfo/CompanyDetails';

import axios from "../../../utils/axios";
import { useQuery } from 'react-query';
import { Height } from '@mui/icons-material';

const CompanyInfoView = () => {
  const [tableIndex, setTableIndex] = useState(-1);
  const [tableData, setTableData] = useState([]);

  const query = useQuery(['companys'], async () => {
    const { data } = await axios.post('/companys/info', { company_type: '발주처' });
    return data.data
  });

  const companyInfoModifiy = () => {
    // 모달창 
  }

  useEffect(() => {
    // setTableData(query.data)


  })

  return (
    <>
      <Header>
        업체정보
      </Header>

      {
        query.isLoading ? (<div> Loading </div>) :
          (
            <Box sx={{ height: "100%" }}>
              <Box sx={{ display: "flex", alignItems: "center" }}>
                <SearchField placeholder='업체명, 연락처 등 검색' companys={query.data} setTableData={setTableData}/>
              </Box>


              <Typography
                sx={{ fontWeight: 600, fontSize: "12px", mt: "19px", mb: "10px" }} >
                총 업체({query.data.length})
              </Typography>
              <Box sx={{ display: "flex", height: '100%' }} >
                <CompanyTable companys={tableData} onTableRowClick={(v)=>{alert()}} />
                <CompanyDetails companyInfo={tableIndex !== -1 ? query.data[tableIndex] : null} />
              </Box>
            </Box>
          )
      }
    </>

  );
}

export default CompanyInfoView;
